import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, TextInput, Button} from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";


const storeSessionId = async (sessionId) => {
    try {
        console.log("store : " + sessionId);
        await AsyncStorage.setItem("sessionId", sessionId);
    } catch (e) {
        console.log("async storage store data error : " + e);
    }
}

const getSessionId = async () => {
    try {
        const sessionId = await AsyncStorage.getItem("sessionId");
        console.log("find : " + sessionId);
        return sessionId;
    } catch (e) {
        console.log("async storage get data error : " + e);
    }
}

const clearStore = async () => {
    try {
        await AsyncStorage.removeItem("sessionId");
    } catch (e) {
        console.log("async storage clear data error : " + e);
    }
}

const LoginScreen = ({navigation}) => {

    const [loginId, onChangeLoginId] = useState("");
    const [password, onChangePassWord] = useState("");
    const [sessionId, setSessionId] = useState(null);
    const [isLogin, setIsLogin] = useState(false);


    useEffect(() => {

    }, [sessionId, isLogin])

    async function login({loginId, password, sessionId}) {

        const data = JSON.stringify({loginId, password, sessionId})


        const json = await axios.post(
            "http://localhost:8080/login",
            data,
            {
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        );
        console.log(json.data.login)
        const receiveSessionId = await json.data.sessionId;

        setSessionId(receiveSessionId);


    }

    return (
        <View style={styles.container}>
            <TextInput style={styles.input}
                       onChangeText={onChangeLoginId}
                       value={loginId}
                       placeholder=" id"/>

            <TextInput style={styles.input}
                       onChangeText={onChangePassWord}
                       value={password}
                       placeholder=" password"/>


            <View style={styles.buttonContainer}>
                <View style={styles.buttonBox}>
                    <Button title="login" onPress={async () => {
                        console.log("====login=====")
                        try {
                            await login({loginId, password, sessionId});
                            console.log("go to");
                            navigation.navigate("FriendsScreen");
                        } catch (e) {
                            console.log("login error = " + e);
                            navigation.navigate("LoginScreen");
                        }

                    }}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="register" onPress={() => navigation.navigate("RegisterScreen")}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="clear store" onPress={() => {
                        console.log("===clear===");
                        clearStore();
                        setSessionId(null);
                        getSessionId();
                    }}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="getSessionId" onPress={() => {
                        console.log('sessionid in the button= ' + sessionId);
                    }}/>
                </View>
            </View>

        </View>

    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center"
    },
    input: {
        borderWidth: 1,
        marginVertical: 10,
        height: 40,
        width: 300,
        borderRadius: 10
    },

    buttonContainer: {
        flexDirection: "row",
        marginVertical: 10
    },
    buttonBox: {
        marginHorizontal: 10
    },

    button: {
        marginHorizontal: 10
    }

});

export default LoginScreen;
