import {Text, View, StyleSheet, Button, FlatList} from "react-native";
import {useEffect, useState} from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

async function findPeople(sessionId){

    try{
        const response = await axios.get("http://localhost:8080/members/" + sessionId);
        alert(JSON.stringify(response));
    }catch(e){
        console.log("axios error : " + e);
    }
}

async function getSessionId(){
    const sessionId = await AsyncStorage.getItem("sessionId");
    return sessionId;
}
const PeopleScreen = () => {

    const [data, setData] = useState([]);
    const [sessionId, setSessionId] = useState("");

    useEffect(() => {

        async function initSessionId(){
            return await getSessionId();
        }

        const result = initSessionId();
        result.then(value => {setSessionId(value)});


    },[sessionId]);


    return (
        <View style={styles.container}>
            <Text>{sessionId}</Text>
            <View style={styles.searchSection}>
                <Button title="Search" onPress={() => {findPeople(sessionId)}}/>
            </View>
            <FlatList data={data}
                      renderItem={({item}) => <View><Text>{item.memberName}</Text></View>}
                      keyExtractor={item => item.memberid}
                      style={{backgroundColor:"yellow"}}
            />
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        alignItems:"center"
    },
    searchSection : {

    }
})

export default PeopleScreen;
