import {Button, FlatList, StyleSheet, Text, TextInput, View} from "react-native";
import {useEffect, useState} from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import {Client, Message, Stomp} from "@stomp/stompjs";

const [chatSessionId, setChatSessionId] = useState("");
const [sessionId, setSessionId] = useState("");
const [memberId , setMemberId] = useState("");
const getMemberId = async () => {
    try{
        return await AsyncStorage.getItem("memberId")
    }catch (e){
        console.log("async storage error : " + e);
    }
}

const client = new Client({
    brokerURL:"ws://192.168.45.2:8080/sloppy-gate",

    connectHeaders :{
        login : "test!"
    },
})
client.onConnect = (frame) => {

    const value = frame.headers["user-name"];
    console.log("name = " + value)
    setChatSessionId(value);
}



const getSessionId = async () =>{
    return await AsyncStorage.getItem("sessionId");
}

const ChatScreen = ({route},navigation) =>{
    const {friendName,friendId} = route.params;

    const data = [
        {id : 1, value : "a", sessionId : "asd"},
        {id : 2, value : "b",sessionId : "asd"},
        {id : 3, value : "c",sessionId : "asd"},
        {id : 4, value : "dasfdoiqnwfoqejfenqofvjqenfqeojqencojqnfqjqeojfqnefq",sessionId : "asd"},
    ];

    const [text, onChangeText] = useState("");
    const [id, setId] = useState(5);
    const [initData, setInitData] = useState(data);

    const addElement = ({id,text}) =>{
        let newArray = [...initData, {id:id,value:text,sessionId:sessionId}];
        setInitData(newArray);
    }

    useEffect(() =>{

        const memberIdPromise = getMemberId();
        memberIdPromise.then(value => setMemberId(value));
        const promise = getSessionId();
        promise.then(value => setSessionId(value));

    },[initData, chatSessionId,memberId]);
    const Content = ({text,chat_sessionId}) => (
        <View style={styles.message_container}>

            {chat_sessionId === chat_sessionId ?
                <View style={styles.my_message_box}>
                    <Text>{chat_sessionId === chat_sessionId}</Text>
                    <Text style={styles.name} >me</Text>
                    <View style={styles.content_box}>
                        <Text style={styles.content}>{text}</Text>
                    </View>
                </View>
                :
                <View style={styles.other_message_box}>
                    <Text style={styles.name}>{friendName}</Text>
                    <View style={styles.content_box}>
                        <Text style={styles.content}>{text}</Text>
                    </View>

                </View>
            }
        </View>

    )

    return (
        <View style={styles.container}>
            <View style={styles.chat_container}>
                <FlatList
                    data={initData}
                    renderItem={({item}) => <Content text={item.value} chat_sessionId={item.sessionId}/>}
                    keyExtractor={item => item.id}
                />
            </View>


            <View style={styles.input_container}>
                <View  style={styles.input_box}>
                    <TextInput value={text} onChangeText={onChangeText} style={styles.t_input} />
                </View>
               <View style={styles.button}>
                   <Button title="send" onPress={() => {
                       addElement({id, text});
                       setId(id+1);
                       onChangeText("");
                       client.publish({
                           destination:"/ws/test",
                           body : JSON.stringify({content:text}),
                           headers : {
                               hello : "world"
                           }
                       })
                   }}/>
               </View>
                <Button title="subscribe" onPress={() => {
                    client.subscribe(
                        "/topic/test", (msg) => {
                            console.log(msg.headers);

                            addElement({id, text});
                            setId(id+1);
                        },
                        {memberId : memberId}
                    )
                }}/>

            </View>
    </View>
)


}

const styles = StyleSheet.create({
    container : {
        flex : 1,
        justifyContent : "center"
    },
    chat_container : {
        flex:7,

    },
    input_container:{
        flex:1,
        borderWidth:1,
        flexDirection : "row",
        alignItems:"center",


    },
    input_box : {
        flex : 4,
        justifyContent:"center",


    },
    button : {
        flex : 1,
        marginHorizontal:4,

    },
    t_input : {
        borderWidth : 1,
        borderRadius : 10,
        height:35,
        marginHorizontal:4,

    },
    my_message_box:{
        margin : 4,
        justifyContent:"center",
        alignItems:"flex-end",
        padding:10
    },
    other_message_box:{
        margin : 4,
        borderRadius :10,
        justifyContent:"center",
        alignItems:"flex-start",
        padding:10
    },
    message_container:{
        margin:3
    },
    name : {
        fontSize:12
    },
    content:{
        fontSize:20
    },
    content_box : {
        borderWidth:1,
        margin :10,
        borderRadius:15,
        minHeight:20,
        padding:10,
        maxWidth:200
    }
})

export default ChatScreen;
