import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, TextInput, Button} from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";


function login({loginId, password, sessionId}) {
    console.log(loginId);
    console.log(password);
    console.log(sessionId);
    const data = JSON.stringify({loginId,password,sessionId})
    console.log(data);
    axios.post(
        "http://localhost:8080/login",
        data,
        {
            headers:{
                'Content-Type': 'application/json'
            }
        }
    ).then(response => {
        const json = response.data;
        const sessionId = json.sessionId;
        storeSessionId(sessionId);
        getSessionId();
    }).catch(err => console.log("axios error : " + err));
}

const storeSessionId = async (sessionId) => {
    try{
        console.log("store : " + sessionId);
        await AsyncStorage.setItem("sessionId",sessionId);
    }catch(e){
        console.log("async storage store data error : " + e);
    }
}

const getSessionId = async () =>{
    try{
        const sessionId = await AsyncStorage.getItem("sessionId");
        console.log("find : "  + sessionId);
        return sessionId;
    }catch (e){
        console.log("async storage get data error : " + e);
    }
}

const clearStore = async () =>{
    try{
        await AsyncStorage.removeItem("sessionId");
    }catch(e){
        console.log("async storage clear data error : " + e);
    }
}

const LoginScreen = ({navigation}) => {

    const [loginId, onChangeLoginId] = useState("");
    const [password, onChangePassWord] = useState("");
    const [sessionId, setSessionId] = useState(null);

    useEffect(() => {

    },[]);

    return(
        <View style={styles.container}>
            <TextInput style={styles.input}
                       onChangeText={onChangeLoginId}
                       value={loginId}
                       placeholder=" id"/>

            <TextInput style={styles.input}
                       onChangeText={onChangePassWord}
                       value={password}
                       placeholder=" password"/>


            <View style={styles.buttonContainer}>
                <View style={styles.buttonBox}>
                    <Button  title="login" onPress={() => {
                        console.log("====login=====")
                        const promise = getSessionId();
                        promise.then(result => setSessionId(result));
                        login({loginId, password, sessionId});
                    }}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="register" onPress={() => navigation.navigate("RegisterScreen")}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="clear store" onPress={() => {
                        console.log("===clear===");
                        clearStore();
                        setSessionId(null);
                        getSessionId();
                    }}/>
                </View>

                <View style={styles.buttonBox}>
                    <Button title="getSessionId" onPress={() => {
                        const promise = getSessionId();
                        promise.then(result => setSessionId(result));
                        console.log(sessionId);
                    }}/>
                </View>
            </View>

        </View>

    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems:"center",
        justifyContent:"center"
    },
    input : {
        borderWidth:1,
        marginVertical:10,
        height:40,
        width:300,
        borderRadius:10
    },

    buttonContainer:{
        flexDirection :"row",
        marginVertical :10
    },
    buttonBox:{
        marginHorizontal : 10
    },

    button : {
        marginHorizontal : 10
    }

});

export default LoginScreen;
